<?php	/**	 *		Rolulul functiilor din acest fisier este sa creeze cate un priview al articolelor	 *  astfel incat sa arate si cat de cat bine, voi mai lucra la mecanism.	 */		function generate_article_priview($article_title, $article_content, $article_photos){		$retval = '<tr><td>';		$retval .= '<a href="?show=home&id='.$article_photos.'" class="article_title">'.$article_title.'</a><br/>';		$retval .= substr($article_content, 0, 80).'....<br>';		$retval .= generate_article_photos($article_photos, 30, 30);		return $retval .= '</td></tr>';		}		function generate_article_photos($article_photos, $width, $height){		$retval = '';		$path = 'database'.DIRECTORY_SEPARATOR.'content'.DIRECTORY_SEPARATOR.'photos'.DIRECTORY_SEPARATOR.$article_photos;				if(file_exists($path) && is_dir($path)){			$dir_content = scandir($path);			foreach($dir_content as $photo){				if(substr($photo, 0, 1) !== '.'){					$retval .= '<img src="'.$path.DIRECTORY_SEPARATOR.$photo.'" width="'.$width.'px" height="'.$height.'px"/>';				}			}		} 		return $retval;	}