<?php	/**	 * Fisier inca in test, deoarece daca fac schimbari gresite in acest fisier pot afecta tot proiectul....	 * De lucart maine la user panel	 * Toata sectiunea logare este inca in varianta de test     */	 	function user_data_exists_and_match($username, $password, $database){		foreach($database as $index => $value){			if($value['username'] === $username && $value['password'] === $password){				return $index;			}		}		return FALSE;	}		function check_if_username_exists($username, $database){		foreach($database as $index => $value){			if($value['username'] === $username){				return TRUE;			}		}		return FALSE;	}	