<?php		/**	 * Scopul acestei functii este sa caute in db la sectiunea content	 * daca se potriveste vre-un cuvant cu cuvantul potrivit apaoi returnewaza 	 *      articolul unde a gasit cuvantul unde se potriveste	 */	 		function search_content($search, $db){		$retval = '<table class="articles">';		foreach($db as $index => $value){			if(in_string($value['content'], $search)){				$retval .= generate_article_priview($value['title'], 					$value['content'], $value['photos']);			}		}		return $retval .= '</table>';	}		function in_string($bigger, $shorter){		$bigger = explode(' ', $bigger);		foreach($bigger as $value){			if($value === $shorter){				return TRUE;			}		}		return FALSE;	}