<?php		function generate_settings_panel($site_address, $site_theme){		return '<center><a href="?show=site_settings&edit=true"> editeaza </a> </center>				<h4>Settings</h4><br/>				<b> adresa: '.$site_address.' </b><br/>			    <b> tema site-ului: '.$site_theme.' </b><br/>				';	}		function generate_settings_form($site_address, $site_theme, $theme_dictionary){		$retval = '<form method="post">';		$retval .= ' ADRESA :<input type="text" name="site_address" value="'.$site_address.'"><br>';		foreach($theme_dictionary as $theme_name => $address){			if($site_theme === $address){				$retval .= '<input type="radio" name="site_theme" value="'.$theme_name.'" checked/>'.$theme_name.'<br>';			}			else {				$retval .= '<input type="radio" name="site_theme" value="'.$theme_name.'"/>'.$theme_name.'<br>';			}		}		return $retval .= '<input type="submit" name="submit" value="salveaza"></form>';	}