<?php	$page_content = '';	$users_db = include __USERSDB__;	if(isset($_GET['make_admin']) && !empty($_GET['make_admin'])){		$users_db[$_GET['make_admin']]['is_admin'] = TRUE;		if(!is_writeable(__USERSDB__)){			chmod(__USERSDB__, 0777);			file_put_contents(__USERSDB__, '<?php return '.var_export($users_db, TRUE).';');		}		else{			file_put_contents(__USERSDB__, '<?php return '.var_export($users_db, TRUE).';');		}		$page_content .= '<meta http-equiv="REFRESH" content="0;url=http://'.__ADDRESS__.'?show=database_management">';	}	else if(isset($_GET['remove']) && !empty($_GET['remove'])){		unset($users_db[$_GET['remove']]);		if(!is_writeable(__USERSDB__)){			chmod(__USERSDB__, 0777);			file_put_contents(__USERSDB__, '<?php return '.var_export($users_db, TRUE).';');		}		else{			file_put_contents(__USERSDB__, '<?php return '.var_export($users_db, TRUE).';');		}		$page_content .= '<meta http-equiv="REFRESH" content="0;url=http://'.__ADDRESS__.'?show=database_management">';	}	else{		foreach($users_db as $id => $userdata){				$page_content .= '<center>';			if($userdata['username'] !== $_SESSION['logat']['username']){				$page_content .= '<a href="http://'.__ADDRESS__.'?show=database_management&remove='.$id.'"> sterge utilizatorul  </a> &nbsp';			}						if($userdata['is_admin'] !== TRUE){				$page_content .= '<a href="http://'.__ADDRESS__.'?show=database_management&make_admin='.$id.'"> promoveaza </a>';			}						$page_content .= '</center>';						$page_content .= display_user_data($userdata['username'], $userdata['password'], $userdata['city'], $userdata['region'], $userdata['sex'], $userdata['email'], $userdata['avatar'], $userdata['age']);			$page_content .= '<b><hr noshade></b>';		}	}